import React, { Component } from "react";
import "../cards/style.css";
export default class Card extends Component {
	constructor(props) {
		super(props);
		this.state = {
			play: false
		};
	}

	static getDerivedStateFromProps(props) {
		return { play: this.state.playAll };
	}

	shouldComponentUpdate(nextProps, nextState) {
		if (
			this.props.data.id !== nextProps.data.id ||
			this.state.play !== nextState.play
		) {
			return true;
		}
		return false;
	}
	togglePlayPause = () => {
		this.setState({ play: !this.state.play });
	};
	render() {
		const images = this.props.data.images;
		const playPause = this.state.play ? "centered play" : "centered pause";
		return (
			<div
				className="card"
				data-test="cardComponent"
				onClick={this.togglePlayPause}
			>
				<img
					data-test="gifImage"
					alt={this.props.data.title}
					loading="lazy"
					src={
						this.state.play ? images.downsized.url : images["480w_still"].url
					}
				/>
				<div data-test="playBtn" className={playPause}>
					<div className="triangle"></div>
				</div>
			</div>
		);
	}
}
